dynamic_elements.count_query:
SELECT (COUNT(?band) AS ?count)
WHERE {
     ?band ?a wd:Q215380 . # music band 
     ?band wdt:P495 wd:Q145 . # from uk
}

SELECT (COUNT(?band) AS ?count)
WHERE {
     ?band ?a wd:Q5741069 . # rock band 
     ?band wdt:P495 wd:Q145 . # from uk
}

dynamic_elements.table_query:
SELECT DISTINCT ?Member ?MemberLabel (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?Image 
WHERE { wd:Q1299 wdt:P527 ?Member . ?Member wdt:P569 ?BirthDate . 
       OPTIONAL {?Member wdt:P570 ?DeathDate.} ?Member wdt:P19 ?BirthPlace. 
       OPTIONAL {?Member wdt:P412 ?VoiceType.} 
       OPTIONAL {?Member wdt:P18 ?Image.} 
       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 

LIMIT 10
