dynamic_elements.table_query:
SELECT DISTINCT ?horse ?horseLabel ?mother ?motherLabel ?father ?fatherLabel (year(?birthdate) as ?birthyear) (year(?deathdate) as ?deathyear) ?genderLabel ?pic
WHERE
{
  ?horse wdt:P31 wd:Q726 .
  ?horse wdt:P25 ?mother .     
  ?horse wdt:P22 ?father .      
  ?horse wdt:P21 ?gender .  
  ?horse wdt:P18 ?pic .
  OPTIONAL {?horse wdt:P569 ?birthdate .}
  OPTIONAL {?horse wdt:P570 ?deathdate .}
 
  SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "en"
  }
}
ORDER BY ?horse
LIMIT 10

dynamic_elements.chart_query:
SELECT  ?label ?count
WHERE {
  ?snake wdt:P7770 ?count . #egg incubation period
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".
          ?snake rdfs:label         ?label.
      }

}

ORDER BY DESC(?count)
LIMIT 10

dynamic_elements.textsearch_query:
SELECT DISTINCT ?pic ?sheep ?sheepLabel ?color ?colorLabel ?status ?statusLabel  ?country ?countryLabel
WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search <<searchterm>>; #country
                      mwapi:language "en".
      ?country wikibase:apiOutputItem mwapi:item.
  }
  ?sheep wdt:P31 wd:Q15622363 .
  ?sheep wdt:P495 ?country .
  ?sheep wdt:P2371 ?status .
  OPTIONAL {?sheep wdt:P18 ?pic ; wdt:P462 ?color }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
  FILTER regex(str(?pic), "jpg").
}
ORDER BY ?sheep
LIMIT 5
