dynamic_elements.textsearch_query:
SELECT DISTINCT ?band ?bandLabel ?category ?categoryLabel ?country ?countryLabel WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search <<searchterm>>;
                      mwapi:language "en".
      ?band wikibase:apiOutputItem mwapi:item.
  }
  {
   {?band wdt:P31 ?category ; wdt:Q38 ?country .
              ?category wdt:P31 wd:Q105756498 . }
    UNION
   {?band wdt:P31 ?category ; wdt:Q38 ?country .
    ?category wdt:P279 wd:Q2088357 }
   }
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 10

dynamic_elements.action_query:
SELECT DISTINCT ?member ?memberLabel ?image
WHERE { 
  <<item>> wdt:P527 ?member . 
 OPTIONAL { ?member wdt:P18 ?image . }

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 20

dynamic_elements.table_query:
SELECT DISTINCT ?Member ?MemberLabel (year(xsd:dateTime(?BirthDate)) as ?Birth) ?BirthPlace ?BirthPlaceLabel ?image
WHERE { wd:Q3841122 wdt:P527 ?Member . ?Member wdt:P569 ?BirthDate . 
       OPTIONAL {?Member wdt:P19 ?BirthPlace.}
       OPTIONAL {?Member wdt:P18 ?image . }
       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 

LIMIT 10

map_filters.map_filter_query:
SELECT DISTINCT ?point ?filter ?filterLabel
WHERE {
?point wdt:P31 wd:Q746369; wdt:P17 wd:Q38 .
?point wdt:P131 ?filter. ?filter rdfs:label ?filterLabel .
FILTER(LANG(?filterLabel) = '' || LANGMATCHES(LANG(?filterLabel), 'en')). }
