dynamic_elements.table_query:
#standardSPARQL
SELECT DISTINCT ?band ?bandLabel ?image
WHERE {
  ?band wdt:P31 wd:Q5741069;        # Instance of: rock band
        wdt:P495 wd:Q38.            # Country of origin: Italy

  OPTIONAL { ?band wdt:P18 ?image . }

  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en".
  }
}
LIMIT 10 OFFSET 10

SELECT DISTINCT ?Member ?MemberLabel (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?VoiceType ?VoiceTypeLabel  

WHERE { wd:Q634852 wdt:P527 ?Member . ?Member wdt:P569 ?BirthDate .  

       OPTIONAL {?Member wdt:P570 ?DeathDate.} ?Member wdt:P19 ?BirthPlace.  

       OPTIONAL {?Member wdt:P412 ?VoiceType.}  

       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} }  

  

LIMIT 10

