dynamic_elements.count_query:
SELECT (COUNT(?band) AS ?count)
WHERE {
     ?band ?a wd:Q5741069 . # music band 
     ?band wdt:P495 wd:Q159 . # from russia
}

dynamic_elements.table_query:
SELECT DISTINCT ?band ?bandLabel ?image 
WHERE { ?band wdt:P31 wd:Q5741069; # Instance of: rock band 
              wdt:P495 wd:Q159. # Country of origin: Russia 
       OPTIONAL { ?band wdt:P18 ?image . } 
       SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". } } 
LIMIT 15 OFFSET 15

dynamic_elements.chart_query:
SELECT (?genreLabel as ?label) 
WHERE { wd:Q949424 wdt:P136 ?genre. ?genre rdfs:label ?genreLabel. 
       FILTER (lang(?genreLabel) = "en"). }
