dynamic_elements.count_query:
SELECT (COUNT(?theatre) AS ?count)
WHERE {
  ?theatre wdt:P31 wd:Q24354; 
           wdt:P17 wd:Q145.
}

SELECT (COUNT(?theater) AS ?count)
WHERE {
  ?theater wdt:P31 wd:Q24354 . 
  ?theater wdt:P131 wd:Q84 .   

  SERVICE wikibase:label { bd:serviceParam wikibase:language "en" }
}

dynamic_elements.table_query:
SELECT DISTINCT ?theatre ?theatreLabel ?image
WHERE {
  ?theatre wdt:P31 wd:Q24354;  
           wdt:P17 wd:Q145.    
  
  OPTIONAL { ?theatre wdt:P18 ?image . }
  
  SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 10
OFFSET 10

dynamic_elements.textsearch_query:
SELECT ?theatre ?theatreLabel ?city ?cityLabel
WHERE {
  SERVICE wikibase:mwapi {
    bd:serviceParam wikibase:endpoint "www.wikidata.org";
                    wikibase:api "EntitySearch";
                    mwapi:search <<searchterm>>;
                    mwapi:language "en".
    ?city wikibase:apiOutputItem mwapi:item.
  }
  ?theatre wdt:P31 wd:Q24354;   
           wdt:P17 wd:Q145.     
  ?theatre wdt:P131 ?city.    

  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ?theatreLabel

dynamic_elements.action_query:
SELECT DISTINCT ?theater ?theaterLabel ?inception 
WHERE { 
  ?theater wdt:P31 wd:Q24354 .   
  ?theater wdt:P17 wd:Q145 .  
  ?theater wdt:P571 ?inception <<item>> .  
 
  SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" .
    ?theatre rdfs:label ?theatreLabel .
  }
}
ORDER BY ?theaterLabel

dynamic_elements.table_query:
SELECT DISTINCT ?theatre ?theatreLabel ?inaugurationYear
WHERE {
  ?theatre wdt:P31 wd:Q24354;    
           wdt:P17 wd:Q145;       
           wdt:P571 ?inaugurationYear.  
  
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ?inaugurationYear
LIMIT 10

