dynamic_elements.table_query:
SELECT DISTINCT ?woodtype ?woodtypeLabel ?image
WHERE { wd:Q6607 wdt:P527 ?woodtype . 
       OPTIONAL { ?woodtype wdt:P18 ?image . }
       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 
LIMIT 12

dynamic_elements.textsearch_query:
SELECT DISTINCT ?guitarName ?woodName 
WHERE {
    SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search <<searchterm>>;
                      mwapi:language "en".
      ?guitar wikibase:apiOutputItem mwapi:item.
    }
  
    ?guitar wdt:P186 ?wood . # Tutte le cose che sono una chitarra fatte da un materiale di tipo legno
    ?wood wdt:P31 wd:Q1493054 . # Tutti i tipi di legni
    ?guitar wdt:P31 wd:Q6607 . # Tutte le chitarre
  
    ?guitar rdfs:label ?guitarName . 
    FILTER (langMatches( lang(?guitarName), "EN" ) )
    
    ?wood rdfs:label ?woodName . 
    FILTER (langMatches( lang(?woodName), "EN" ) )  
  
   
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 10

dynamic_elements.table_query:
SELECT ?country ?countryLabel (COUNT(?guitarist) AS ?count) WHERE {
  ?guitarist wdt:P1303 wd:Q6607; # plays guitar
    wdt:P27 ?country. # country of citizenship
  SERVICE wikibase:label { bd:serviceParam wikibase:language "it" . }
} GROUP BY ?country ?countryLabel
ORDER BY DESC(?count)

LIMIT 20
