dynamic_elements.table_query:
SELECT DISTINCT ?components ?componentsLabel ?image
WHERE { wd:Q6607 wdt:P527 ?components . 
       OPTIONAL { ?components wdt:P18 ?image . }
       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 
LIMIT 12

dynamic_elements.textsearch_query:
SELECT DISTINCT ?guitar ?guitarLabel ?wood ?woodLabel
WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search <<searchterm>>; #country
                      mwapi:language "en".
      ?guitar wikibase:apiOutputItem mwapi:item.
  }
  ?guitar wdt:P186 ?wood .
  ?wood wdt:P31 wd:Q1493054 .
  ?guitar wdt:P31 wd:Q6607 .
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}
ORDER BY ?guitar

dynamic_elements.action_query:
SELECT DISTINCT ?image ?imageLabel
WHERE { 
  <<item>> wdt:P18 ?image.

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
