dynamic_elements.count_query:
SELECT (COUNT(?band) AS ?count)
WHERE {
     ?band ?a wd:Q215380 . # music band 
     ?band wdt:P495 wd:Q30 . # from 
}

SELECT (COUNT(?band) AS ?count)
WHERE {
     ?band ?a wd:Q5741069 . # rock band 
     ?band wdt:P495 wd:Q30 . # from uk
}

dynamic_elements.map_points_query:
SELECT DISTINCT ?point ?pointLabel ?villeIdLabel ?lat ?long
WHERE {
?point wdt:P31 wd:Q483110; wdt:P17 wd:Q30;
wdt:P131 ?villeId; p:P625 ?statement.
?statement psv:P625 ?node. ?node wikibase:geoLatitude ?lat ; wikibase:geoLongitude ?long.
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". } }

dynamic_elements.table_query:
SELECT DISTINCT ?Member ?MemberLabel (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?occupation ?occupationLabel 
WHERE { wd:Q11895 wdt:P527 ?Member . ?Member wdt:P569 ?BirthDate . 
OPTIONAL {?Member wdt:P570 ?DeathDate.} ?Member wdt:P19 ?BirthPl 
OPTIONAL {?Member wdt:P1303 ?occupation.} 
SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 
LIMIT 10

dynamic_elements.textsearch_query:
SELECT DISTINCT ?components ?componentsLabel ?image 
WHERE { wd:Q10708 wdt:P527 ?components . 
       OPTIONAL { ?components wdt:P18 ?image . } 
       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 
LIMIT 10
