dynamic_elements.count_query:
SELECT (COUNT(?girlband) AS ?count)
WHERE {
     ?girlband ?a wd:Q641066 .
     ?girlband wdt:P495 wd:Q30 .
}

dynamic_elements.table_query:
SELECT DISTINCT ?Member ?MemberLabel (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?VoiceType ?VoiceTypeLabel ?Pic 
WHERE { wd:Q530886 wdt:P527 ?Member . ?Member wdt:P569 ?BirthDate . 
       OPTIONAL {?Member wdt:P570 ?DeathDate.} ?Member wdt:P19 ?BirthPlace. 
       OPTIONAL {?Member wdt:P412 ?VoiceType.} 
       OPTIONAL {?Member wdt:P18 ?Pic.} 
       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 

LIMIT 10
