dynamic_elements.textsearch_query:
SELECT DISTINCT ?composer ?composerLabel ?birthPlace ?birthPlaceLabel ?pic
WHERE {
  SERVICE wikibase:mwapi {
   bd:serviceParam wikibase:endpoint "www.wikidata.org" ;
         	wikibase:api "EntitySearch" ;
         	mwapi:search <<searchterm>> ;
         	mwapi:language "en".
   ?birthPlace wikibase:apiOutputItem mwapi:item.
  }
 
 ?composer wdt:P106 wd:Q36834 .
 ?composer wdt:P19 ?birthPlace .
 OPTIONAL {?composer wdt:P18 ?pic}
 SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
 FILTER regex(str(?pic), "jpg").
} 
ORDER BY ?composer
LIMIT 10

dynamic_elements.table_query:
SELECT DISTINCT (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?DeathPlace ?DeathPlaceLabel
WHERE {
wd:Q168485 wdt:P569 ?BirthDate .
wd:Q168485 wdt:P570 ?DeathDate .
wd:Q168485 wdt:P19 ?BirthPlace .
wd:Q168485 wdt:P20 ?DeathPlace .
SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}

dynamic_elements.chart_query:
SELECT DISTINCT ?label (COUNT(distinct ?composer) as ?count)
WHERE {
        	?composer ?a wd:Q36834 . # composer
        	?composer wdt:P19 wd:Q124139 . #Born in Maiolata Spontini
	
        	SERVICE wikibase:label { bd:serviceParam wikibase:language "en".
  	  	?composer rdfs:label 	    	?label.
        	}
}
 
GROUP BY ?label ?count
ORDER BY DESC(?count)
