dynamic_elements.table_query:
SELECT DISTINCT (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?DeathPlace ?DeathPlaceLabel

WHERE { 
wd:Q168485 wdt:P569 ?BirthDate . 
wd:Q168485 wdt:P570 ?DeathDate . 
wd:Q168485 wdt:P19 ?BirthPlace . 
wd:Q168485 wdt:P20 ?DeathPlace . 
SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}

dynamic_elements.map_points_query:
SELECT DISTINCT ?point ?pointLabel ?lat ?long
WHERE
{
  wd:Q168485 wdt:P27 ?point .
  ?point p:P625 ?statement.
  ?statement psv:P625 ?node.
  ?node wikibase:geoLatitude ?lat.
  ?node wikibase:geoLongitude ?long.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

dynamic_elements.table_query:
SELECT DISTINCT ?occupation ?occupationLabel 

WHERE {
  wd:Q168485 wdt:P106 ?occupation .
  ?occupation rdfs:label ?occupationLabel.
  
  FILTER(LANG(?occupationLabel)="en").
}

SELECT DISTINCT ?fieldofwork ?fieldofworkLabel

WHERE {
  wd:Q168485 wdt:P101 ?fieldofwork .
  ?fieldofwork rdfs:label ?fieldofworkLabel.
 
  FILTER(LANG(?fieldofworkLabel)="en").
}

