SELECT DISTINCT ?award ?awardLabel ?serviceribbonimage ?image
WHERE {
  wd:Q168485 wdt:P166 ?award.
  OPTIONAL { ?award wdt:P2425 ?serviceribbonimage }
  OPTIONAL { ?award wdt:P18 ?image }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}

