dynamic_elements.table_query:
SELECT DISTINCT ?band ?bandLabel  ?influencedbyLabel ?country ?countryLabel 
WHERE {
    ?band wdt:P31 ?category ; 
          wdt:P495 ?country ; 
          wdt:P136 wd:Q183504 .
    ?category wdt:P279 wd:Q2088357 .
    ?others wdt:P737 ?band
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 10

SELECT DISTINCT ?member ?memberLabel ?image
WHERE { 
  wd:Q485820 wdt:P527 ?member . 
 OPTIONAL { ?member wdt:P18 ?image . }

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 4

SELECT ?album ?albumLabel ?year ?yearLabel
WHERE {
  ?band wdt:P31 wd:Q215380.                 # Specify that the entity is a band
  ?band rdfs:label "Blur"@en.               # Specify the band's name (in this case, "Blur")
  ?album wdt:P31 wd:Q482994.                # Specify that the entity is an album
  ?album wdt:P175 ?band.                    # Specify that the album is released by the band
  ?album wdt:P577 ?year.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC (?year)

dynamic_elements.count_query:
SELECT (COUNT(?award) AS ?count)
WHERE {
  ?blur wdt:P31 wd:Q2088357.  
  ?blur wdt:P166 ?award.       
}

dynamic_elements.map_points_query:
SELECT ?person ?personLabel ?birthplace ?birthplaceLabel ?node ?nodeLabel ?lat ?long
WHERE {
  ?person wdt:P463 wd:Q485820 .   
  ?person wdt:P19 ?birthplace .


  ?birthplace p:P625 ?statement.
  ?statement psv:P625 ?node.
  ?node wikibase:geoLatitude ?lat.
  ?node wikibase:geoLongitude ?long.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  }

SELECT ?song ?songLabel ?recordingstudio ?recordingstudioLabel ?node ?nodeLabel ?lat ?long 
WHERE {
   ?song wdt:P483 ?recordingstudio ;
          wdt:P175 wd:Q485820 .
  ?recordingstudio p:P625 ?statement.
  ?statement psv:P625 ?node.
  ?node wikibase:geoLatitude ?lat.
  ?node wikibase:geoLongitude ?long.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  }
