dynamic_elements.count_query:
SELECT (SUM(?followers) AS ?count)
WHERE {
    ?band ?a wd:Q215380.    
    ?band wdt:P8687 ?followers . 
    FILTER (?band = wd:Q485820)  
}

dynamic_elements.map_points_query:
SELECT ?song ?songLabel ?recordingstudio ?recordingstudioLabel ?node ?nodeLabel ?lat ?long 
WHERE {
   ?song wdt:P483 ?recordingstudio ;
          wdt:P175 wd:Q485820 .
  ?recordingstudio p:P625 ?statement.
  ?statement psv:P625 ?node.
  ?node wikibase:geoLatitude ?lat.
  ?node wikibase:geoLongitude ?long.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  }

SELECT ?person ?personLabel ?birthplace ?birthplaceLabel ?node ?nodeLabel ?lat ?long
WHERE {
  ?person wdt:P463 wd:Q188626 .   
  ?person wdt:P19 ?birthplace .


  ?birthplace p:P625 ?statement.
  ?statement psv:P625 ?node.
  ?node wikibase:geoLatitude ?lat.
  ?node wikibase:geoLongitude ?long.
  SERVICE wikibase:label { bd:serviceParam wikibase:language"en".}
}
