dynamic_elements.count_query:
SELECT (COUNT(?band) AS ?count)
WHERE {
 ?band ?a wd:Q215380 . # music band
     ?band wdt:P495 wd:Q218 . # from romania
}
LIMIT 10

dynamic_elements.table_query:
SELECT DISTINCT ?band ?bandLabel ?image 
WHERE { ?band ?a wd:Q215380 . # music band  
              ?band wdt:P495 wd:Q218. # Country of origin: Romania
       OPTIONAL { ?band wdt:P18 ?image . } 
                                                      SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". } } 
LIMIT 15 OFFSET 15
