dynamic_elements.chart_query:
PREFIX amr: <http://www.ontologydesignpatterns.org/ont/amr/>
SELECT DISTINCT  ?label (COUNT(distinct ?subject) as ?count)
WHERE {
    ?subject a amr:Organization ;
<http://www.ontologydesignpatterns.org/ont/amr/name> ?label .


FILTER (!regex(str(?label), "jstor",'i'))
    }
GROUP BY ?label ?count 
ORDER BY DESC (?count)
LIMIT 10

dynamic_elements.table_query:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX amr: <http://www.ontologydesignpatterns.org/ont/amr/>

 

SELECT DISTINCT ?Organization (YEAR(?foundationDate) as ?foundationYear) 

WHERE {
  ?s a amr:Organization ;
    owl:sameAs ?wikidata ;
    amr:name ?slabel ;
?isconnectedto ?cityUri .

  ?cityUri rdf:type amr:City ;
    owl:sameAs ?point ;
    amr:name ?label .

  ?frameInstance ?p ?cityUri ;
    rdf:type ?frame .
   FILTER regex (?label, "Bologna", "i")

 

  
  FILTER (regex(str(?wikidata), "http://www.wikidata.org/entity/", "i"))

 

  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql>
       {
    ?wikidata  rdfs:label ?Organization . 
     OPTIONAL  { ?wikidata wdt:P571 ?foundationDate . }     
    FILTER (langMatches(lang(?Organization), "en"))
 FILTER (YEAR(?foundationDate) != 1350)
  }
}
ORDER BY ASC (?foundationYear)

PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT DISTINCT (year(?birthDate) as ?birthYear)?birthPlace (year(?deathDate) as ?deathYear) ?deathPlace ?occupation ?pic
WHERE
{
SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql>
{
wd:Q207532  wdt:P18 ?pic .
OPTIONAL { wd:Q207532  wdt:P569 ?birthDate . }
OPTIONAL { wd:Q207532  wdt:P19 ?bplace . ?bplace rdfs:label ?birthPlace . }
OPTIONAL { wd:Q207532  wdt:P570 ?deathDate . }
OPTIONAL { wd:Q207532  wdt:P20 ?dplace. ?dplace rdfs:label ?deathPlace . }
OPTIONAL { wd:Q207532  wdt:P106 ?occ . ?occ rdfs:label ?occupation .  }
  
FILTER langMatches(lang(?birthPlace), 'en')
FILTER langMatches(lang(?deathPlace), 'en')
FILTER langMatches(lang(?occupation), 'en')
}
}
OFFSET 65
LIMIT 1

dynamic_elements.textsearch_query:
PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX bd: <http://www.bigdata.com/rdf#>
PREFIX wikibase: <http://wikiba.se/ontology#>
PREFIX mwapi: <https://www.mediawiki.org/ontology#API/>
PREFIX bds: <http://www.bigdata.com/rdf/search#>
SELECT DISTINCT ?violinist (year(?birthDate) as ?birthYear) ?birthPlace (year(?deathDate) as ?deathYear) ?deathPlace ?citizenship ?pic

WHERE {
  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {

    SERVICE wikibase:mwapi {

      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search <<searchterm>>; #violinist
                      mwapi:language "en".
      ?viol wikibase:apiOutputItem mwapi:item.
  }

 ?viol wdt:P106 wd:Q1259917 ;
       rdfs:label ?violinist ; 
OPTIONAL {?viol wdt:P27 ?citiz . ?citiz rdfs:label ?citizenship . }
OPTIONAL {?viol wdt:P569 ?birthDate . }
OPTIONAL {?viol wdt:P19 ?bplace . ?bplace rdfs:label ?birthPlace . }
OPTIONAL {?viol wdt:P570 ?deathDate .}
OPTIONAL {?viol wdt:P20 ?dplace. ?dplace rdfs:label ?deathPlace . }
OPTIONAL {?viol wdt:P18 ?pic . }

SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
FILTER regex(str(?pic), "jpg").
FILTER langMatches(lang(?violinist), 'en')
FILTER langMatches(lang(?birthPlace), 'en')
FILTER langMatches(lang(?deathPlace), 'en')
FILTER langMatches(lang(?citizenship), 'en')

}

}

LIMIT 1

dynamic_elements.table_query:
PREFIX dpo: <http://dbpedia.org/ontology/>
PREFIX dpp: <http://dbpedia.org/property/>
PREFIX dbr: <http://dbpedia.org/resource/>

SELECT DISTINCT ?name (SAMPLE(?depiction) as ?sampleDepiction) 
WHERE {
  SERVICE <https://dbpedia.org/sparql> {
    { ?s a dpo:Agent . }
    UNION
    { ?s a <http://dbpedia.org/class/yago/WikicatViolins> . }
    ?s dpp:wikiPageUsesTemplate <http://dbpedia.org/resource/Template:Violin_family> ;
       rdfs:label ?name.
    FILTER langMatches(lang(?name), "en")
    OPTIONAL { ?s foaf:depiction ?depiction }
    OPTIONAL { ?s dpo:soundRecording ?sound }
  FILTER (regex(str(?depiction), ".jpg"))
  }
}
GROUP BY ?name
ORDER BY ASC(?name)
