dynamic_elements.count_query:
SELECT (COUNT (?item) as ?count) 

WHERE {
  ?item wdt:P31 wd:Q482994.
  
  SERVICE wikibase:label {bd:serviceParam wikibase:language "en".}
}

SELECT (COUNT(?item) AS ?count)
WHERE {
  ?item wdt:P31 wd:Q482994;
        wdt:P577 ?date. 
  FILTER(YEAR(?date) <= 2020 && YEAR(?date) > 1999)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

SELECT (COUNT(?item) AS ?count)
WHERE {
  ?item wdt:P31 wd:Q482994;
        wdt:P577 ?date;
wdt:P437 ?format. 
  FILTER(YEAR(?date) <= 2020 && YEAR(?date) > 1999)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

dynamic_elements.chart_query:
SELECT ?label (COUNT(?album) as ?count)
WHERE {
  ?album wdt:P31 wd:Q482994;
         wdt:P437 ?format;
         wdt:P577 ?date.
  
  FILTER(YEAR(?date) <= 2020 && YEAR(?date) > 1999)
  
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?format rdfs:label ?label.
  }

  VALUES ?format { wd:Q178588 wd:Q841983 wd:Q34467 wd:Q15982450 }  # Vinyl, LP, Compact disc, Streaming service
  BIND(IF(?format IN (wd:Q178588, wd:Q841983), "vinyl record", ?label) AS ?label)

}
GROUP BY ?label
ORDER BY DESC(?count)

SELECT DISTINCT ?label (COUNT(?item) AS ?count)
WHERE {
     ?item ?a wd:Q482994;
           wdt:P437 wd:Q34467; # CD
           wdt:P577 ?date.

  BIND(YEAR(?date) AS ?label) FILTER(YEAR(?date) <= 2020 && YEAR(?date) > 1999)
}

GROUP BY ?label ?count
ORDER BY ?label

SELECT DISTINCT ?label (COUNT(?item) AS ?count)

WHERE {
     ?item ?a wd:Q15590336;
           wdt:P571 ?date.

  BIND(YEAR(?date) AS ?label)
}
GROUP BY ?label ?count
ORDER BY ?label

dynamic_elements.table_query:
SELECT ?platform ?platformLabel (SUM(?followers) AS ?totalFollowers) ?year ?country ?countryLabel
WHERE {
  ?platform wdt:P31 wd:Q15590336 ;
           wdt:P8687 ?followers .
  
  
  OPTIONAL { ?platform wdt:P571 ?date. }
  OPTIONAL { ?platform wdt:P495 ?country . }
  OPTIONAL { ?platform wdt:P17 ?country . }
  
  BIND(YEAR(?date) AS ?year)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en, it, fr, ja, nl".}
}

GROUP BY ?platform ?platformLabel ?year ?country ?countryLabel
ORDER BY DESC(?totalFollowers)
LIMIT 14

dynamic_elements.map_points_query:
SELECT ?point ?pointLabel ?headquartersLabel ?lat ?long
WHERE {
  ?point wdt:P31 wd:Q15590336 ;
         wdt:P159 ?headquarters.

  ?headquarters p:P625 ?statement.
  ?statement psv:P625 ?node.
  ?node wikibase:geoLatitude ?lat.
  ?node wikibase:geoLongitude ?long.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  }

dynamic_elements.textsearch_query:
SELECT DISTINCT ?album ?albumLabel ?performer ?performerLabel ?date
WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search <<searchterm>>;
                      mwapi:language "en".
      ?album wikibase:apiOutputItem mwapi:item. }

  {?album ?a wd:Q482994; wdt:P175 ?performer .
   ?album wdt:P577 ?date; wdt:P437 ?format .}

SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" }
}

LIMIT 20

dynamic_elements.action_query:
SELECT DISTINCT ?format ?formatLabel
WHERE { 
  <<item>> wdt:P437 ?format .

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 20

SELECT DISTINCT ?genre ?genreLabel
WHERE { 
  <<item>> wdt:P136 ?genre .

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 20
