PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wikibase: <http://wikiba.se/ontology#>
SELECT ?h ?hLabel ?cause ?causeLabel (YEAR(?date) AS ?year) WHERE {
?h wdt:P463 wd:Q326461;
   wdt:P509 ?cause;
   wdt:P570 ?date
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} ORDER BY ?year

dynamic_elements.textsearch_query:
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wikibase: <http://wikiba.se/ontology#>
SELECT DISTINCT ?singer ?singerLabel  ?genre ?genreLabel  ?voice ?voiceLabel ?band ?bandLabel    WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search "kurt cobain"
                     ;
                      mwapi:language "en".
      ?singer wikibase:apiOutputItem mwapi:item.
  }

   {?singer wdt:P31 wd:Q5; wdt:P136 ?genre .
    ?singer wdt:P31 wd:Q5; wdt:P412 ?voice .

              ?category wdt:P31 wd:Q177220 . 
  
    
   }
   OPTIONAL {?singer wdt:P463 ?band .}
     
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
