dynamic_elements.textsearch_query:
SELECT DISTINCT ?violinist ?violinistLabel ?birthPlace ?birthPlaceLabel ?pic
WHERE {
  SERVICE wikibase:mwapi {
   bd:serviceParam wikibase:endpoint "www.wikidata.org" ;
         	wikibase:api "EntitySearch" ;
         	mwapi:search <<searchterm>> ;
         	mwapi:language "en".
   ?birthPlace wikibase:apiOutputItem mwapi:item.
  }
 
 ?violinist wdt:P106 wd:Q1259917 .
 ?violinist wdt:P19 ?birthPlace .
 OPTIONAL {?violinist wdt:P18 ?pic}
 SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
 FILTER regex(str(?pic), "jpg").
} 
ORDER BY ?violinist
LIMIT 10

dynamic_elements.table_query:
SELECT DISTINCT (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?DeathPlace ?DeathPlaceLabel

WHERE { 
wd:Q7302 wdt:P569 ?BirthDate . 
wd:Q7302 wdt:P570 ?DeathDate . 
wd:Q7302 wdt:P19 ?BirthPlace . 
wd:Q7302 wdt:P20 ?DeathPlace . 
SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}

SELECT DISTINCT ?genre ?genreLabel
WHERE {
  wd:Q7302 wdt:P136 ?genre. # genre
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}

SELECT DISTINCT ?musicalwork ?musicalworkLabel ?publicationdate
WHERE {
  wd:Q7302 wdt:P800 ?musicalwork. # musical work
  ?musicalwork wdt:P577 ?publicationdate. #publication date
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}
