dynamic_elements.count_query:
SELECT (COUNT(?StringInstrument) AS ?count) 
WHERE { 
 	?StringInstrument ?a wd:Q1798603 . # string instrument 
}

SELECT (COUNT(?BowedStringInstrument) AS ?count) 
WHERE { 
 	?BowedStringInstrument ?a wd:Q192096 . # bowed string instrument 
}

dynamic_elements.table_query:
SELECT DISTINCT ?components ?componentsLabel ?image 
WHERE { 
wd:Q8355 wdt:P527 ?components .
OPTIONAL { ?components wdt:P18 ?image . }
 SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}
 LIMIT 9

SELECT DISTINCT ?ViolinType ?ViolinTypeLabel ?image
WHERE {
  ?ViolinType wdt:P279 wd:Q8355 . 
  OPTIONAL { ?ViolinType wdt:P18 ?image . }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}
 LIMIT 10

