dynamic_elements.table_query:
#defaultView:ImageGrid
SELECT ?item ?itemLabel ?pic
WHERE 
 { 
?item wdt:P31 wd:Q5994 . 
   ?item wdt:P18 ?pic
         SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" } 
    }

SELECT DISTINCT ?men ?menLabel
WHERE
 { 
   ?men wdt:P31 wd:Q5 .
   ?men wdt:P21 wd:Q6581097 .
   ?men wdt:P106/wdt:P279* wd:Q486748 . # pianists
   SERVICE wikibase:label {bd:serviceParam wikibase:language "en" }
   }
LIMIT 10

SELECT DISTINCT ?women ?womenLabel
WHERE
 { 
   ?women wdt:P31 wd:Q5 .
   ?women wdt:P21 wd:Q6581072 .
   ?women wdt:P106/wdt:P279* wd:Q486748 . # pianists
   SERVICE wikibase:label {bd:serviceParam wikibase:language "en" }
   }
LIMIT 10

SELECT DISTINCT (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?DeathPlace ?DeathPlaceLabel




WHERE { 
wd:Q255 wdt:P569 ?BirthDate . 
wd:Q255 wdt:P570 ?DeathDate . 
wd:Q255 wdt:P19 ?BirthPlace . 
wd:Q255 wdt:P20 ?DeathPlace . 
SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}
}

SELECT ?notablework ?image ?notableworkLabel 
WHERE { wd:Q255 wdt:P800 ?notablework. ?notablework wdt:P18 ?image. ?notablework rdfs:label ?notableworkLabel 
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". } 
       FILTER(LANG(?notableworkLabel)="en"). }
