dynamic_elements.chart_query:
SELECT DISTINCT ?label (COUNT(DISTINCT ?occupation) AS ?count) WHERE {
  ?luciodalla ?a wd:Q167546;
    wdt:P106 ?occupation.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?occupation rdfs:label ?label.
  }
}
GROUP BY ?label ?count
ORDER BY DESC (?count)
LIMIT 20

dynamic_elements.count_query:
SELECT (COUNT(?singer) AS ?count)
WHERE {
     ?singer ?a wd:Q482994 . # album
     ?singer wdt:P175 wd:Q167546 . # from lucio dalla
}

dynamic_elements.table_query:
SELECT DISTINCT (?label AS ?album) (YEAR(xsd:dateTime(?date)) AS ?date) WHERE {
  ?album wdt:P31 wd:Q482994;
    wdt:P175 wd:Q167546;
    wdt:P577 ?date.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?album rdfs:label ?label.
  }
}
GROUP BY ?label ?date
ORDER BY (?date)

dynamic_elements.chart_query:
SELECT DISTINCT (YEAR(xsd:dateTime(?date)) AS ?label) (COUNT(?label) AS ?count) WHERE {
  ?item wdt:P175 wd:Q167546;
    wdt:P577 ?date.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?item rdfs:label ?label.
  }
}
GROUP BY ?count ?date
ORDER BY (?label)
LIMIT 100

dynamic_elements.table_query:
SELECT DISTINCT ?label (COUNT(DISTINCT ?nomination) AS ?count) WHERE {
  ?lucio ?a wd:Q167546;
    wdt:P1411 ?nomination.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?nomination rdfs:label ?label.
  }
}
GROUP BY ?label ?count
ORDER BY DESC (?count)
LIMIT 20

SELECT DISTINCT ?Awards ?AwardsLabel WHERE {
  wd:Q167546 wdt:P166 ?Awards.
  ?Awards rdfs:label ?AwardsLabel.
  FILTER((LANG(?AwardsLabel)) = "en")
}

dynamic_elements.map_points_query:
SELECT DISTINCT ?point ?pointLabel ?villeIdLabel ?lat ?long
WHERE { 
  ?point wdt:P31 wd:Q174782; wdt:P131 wd:Q1891;
         wdt:P131 ?villeId; p:P625 ?statement.
         ?statement psv:P625 ?node.?node wikibase:geoLatitude ?lat ; wikibase:geoLongitude ?long.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". } }

dynamic_elements.table_query:
SELECT DISTINCT ?datebirth ?datebirthLabel ?datedeath ?datedeathLabel WHERE {
  wd:Q167546 wdt:P569 ?datebirth;
    wdt:P570 ?datedeath.
}
