dynamic_elements.count_query:
SELECT (COUNT(?band) AS ?count)
WHERE {
     ?band ?a wd:Q215380 . # music band 
     ?band wdt:P495 wd:Q30 . # from usa
}

dynamic_elements.map_points_query:
SELECT DISTINCT ?point ?pointLabel ?villeIdLabel ?lat ?long
WHERE {
?point wdt:P31 wd:Q746369; wdt:P17 wd:Q30;
wdt:P131 ?villeId; p:P625 ?statement.
?statement psv:P625 ?node. ?node wikibase:geoLatitude ?lat ; wikibase:geoLongitude ?long.
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". } }

dynamic_elements.textsearch_query:
SELECT DISTINCT ?band ?bandLabel ?category ?categoryLabel ?country ?countryLabel WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search "red hot chili peppers";
                      mwapi:language "en".
      ?band wikibase:apiOutputItem mwapi:item.
  }
  {
   {?band wdt:P31 ?category ; wdt:P495 ?country .
              ?category wdt:P31 wd:Q105756498 . }
    UNION
   {?band wdt:P31 ?category ; wdt:P495 ?country .
    ?category wdt:P279 wd:Q2088357 }
   }
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 10

dynamic_elements.action_query:
SELECT DISTINCT ?genre ?genreLabel
WHERE { 
  <<item>> wdt:P136 ?genre .

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 10

SELECT DISTINCT ?member ?memberLabel ?image
WHERE { 
 wd:Q10708 wdt:P527 ?member . 
 OPTIONAL { ?member wdt:P18 ?image . }

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 20

SELECT DISTINCT ?band ?bandLabel ?category ?categoryLabel ?country ?countryLabel WHERE {
    ?band wdt:P31 ?category ; wdt:P495 ?country ; wdt:P136 wd:Q11399 .
    ?category wdt:P279 wd:Q2088357 
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 20
