dynamic_elements.count_query:
PREFIX owl: <http://www.w3.org/2002/07/owl#> 
PREFIX wd: <http://www.wikidata.org/entity/> 
PREFIX wdt: <http://www.wikidata.org/prop/direct/> 


SELECT DISTINCT (COUNT(distinct ?label) AS ?count)

WHERE {
  ?s rdf:type <http://www.ontologydesignpatterns.org/ont/amr/Person>;          
    owl:sameAs ?wikidata . 
  FILTER (regex(str(?wikidata), "http://www.wikidata.org/entity/", "i")) 

	SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql>  {
	?wikidata wdt:P106 wd:Q36834 ; 
	 rdfs:label ?label .
               FILTER(lang(?label) = "en")
  } 

}

dynamic_elements.map_points_query:
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX p: <http://www.wikidata.org/prop/>
PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/> 
PREFIX psv: <http://www.wikidata.org/prop/statement/value/> 
PREFIX wikibase: <http://wikiba.se/ontology#>
PREFIX bd: <http://www.bigdata.com/rdf#>

SELECT DISTINCT ?point ?lat ?long

WHERE {
  ?s a <http://www.ontologydesignpatterns.org/ont/amr/Person>; 
    owl:sameAs ?point .
  FILTER (regex(str(?point), "http://www.wikidata.org/entity/", "i")) 
SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {
?point wdt:P106 wd:Q36834 .
?point wdt:P27 ?country .
?country p:P625 ?statement .  

?statement psv:P625 ?node. ?node wikibase:geoLatitude ?lat ; wikibase:geoLongitude ?long.
SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} 
}
}

dynamic_elements.table_query:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX wd: <http://www.wikidata.org/entity/>

SELECT ?memberName (YEAR(?birthDate) AS ?birthyear) ?birthPlace   (YEAR(?deathDate) AS ?deathyear)  ?occupation ?pic

WHERE{
  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {
?member wdt:P463 wd:Q190390; # is member of the 5
        rdfs:label ?memberName
    FILTER (lang(?memberName) = "en")

OPTIONAL { ?member  wdt:P569 ?birthDate . }
OPTIONAL { ?member  wdt:P19 ?bplace . ?bplace rdfs:label ?birthPlace . }
  FILTER (lang(?birthPlace) = "en")
OPTIONAL { ?member  wdt:P570 ?deathDate . }

OPTIONAL { ?member  wdt:P106 ?occ . ?occ rdfs:label ?occupation .  }   
    FILTER (lang(?occupation) = "en")
    FILTER (regex(?occupation, "chemist", "i") || regex(?occupation, "military personnel", "i") || regex(?occupation, "pedagogue", "i")) 
OPTIONAL {?member  wdt:P18 ?pic.}
  }
}
ORDER BY ASC (?memberName)

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX wd: <http://www.wikidata.org/entity/>
SELECT ?name ?audio
WHERE{
  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {
{?member wdt:P463 wd:Q190390; #member of the five
      wdt:P800 wd:Q285976;
        rdfs:label ?name. 
   wd:Q285976 wdt:P51 ?audio .    
      FILTER (lang(?name) = "en")
  }
}
}

dynamic_elements.textsearch_query:
PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX bd: <http://www.bigdata.com/rdf#>
PREFIX wikibase: <http://wikiba.se/ontology#>
PREFIX mwapi: <https://www.mediawiki.org/ontology#API/>
SELECT DISTINCT ?composer  (year(?birthDate) as ?birthYear)?birthPlace (year(?deathDate) as ?deathYear) ?deathPlace ?pic 
WHERE {
  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {
       FILTER CONTAINS (?birthPlace, <<searchterm>>). 
 	?person wdt:P136 wd:Q9730 ; #<has genre> <classical music>
            rdfs:label ?composer ;
          wdt:P19 ?bplace. 
          ?bplace rdfs:label ?birthPlace .

OPTIONAL { ?person  wdt:P569 ?birthDate . }
OPTIONAL { ?person  wdt:P570 ?deathDate . }
OPTIONAL { ?person  wdt:P20 ?dplace. ?dplace rdfs:label ?deathPlace . }
  OPTIONAL { ?person wdt:P18 ?pic . }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  FILTER regex(str(?pic), "jpg").
  FILTER (lang(?composer) = "en")
  FILTER (lang(?birthPlace) = "en")
 FILTER (lang(?deathPlace) = "en")
FILTER (YEAR(?birthDate) > 1799 && YEAR(?birthDate) < 1901 )
} }
LIMIT 10
