dynamic_elements.count_query:
SELECT (COUNT(?singer) AS ?count)
WHERE {
     ?singer ?a wd:Q482994 .
     ?singer wdt:P175 wd:Q1056805 .
}

dynamic_elements.table_query:
SELECT DISTINCT (?label AS ?album) (YEAR(xsd:dateTime(?date)) AS ?date) WHERE {
  ?album wdt:P31 wd:Q482994;
    wdt:P175 wd:Q1056805;
    wdt:P577 ?date.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?album rdfs:label ?label.
  }
}
GROUP BY ?label ?date
ORDER BY (?date)

SELECT DISTINCT (?label AS ?album) (YEAR(xsd:dateTime(?date)) AS ?date) WHERE {
  ?album wdt:P31 wd:Q482994;
    wdt:P175 wd:Q3841122;
    wdt:P577 ?date.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?album rdfs:label ?label.
  }
}
GROUP BY ?label ?date
ORDER BY (?date)

dynamic_elements.chart_query:
SELECT DISTINCT (?genre AS ?label) (COUNT(?label) AS ?count) WHERE {
  ?item wdt:P175 wd:Q1056805;
   wdt:P136 ?genre.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?item rdfs:label ?label.
  }
}
GROUP BY ?count ?genre
ORDER BY (?label)
LIMIT 50

dynamic_elements.textsearch_query:
SELECT DISTINCT ?singers ?singersLabel ?category ?categoryLabel   WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search "Cremonini";
                      mwapi:language "it".
      ?singer wikibase:apiOutputItem mwapi:item.
  }
  {
   {?singers wdt:P31 ?category ; wdt:P495  wd:Q38 .
              ?category wdt:P31 wd:Q60723829 . }
    UNION
   {?singers wdt:P31 ?category ; wdt:P495 wd:Q38.
    ?category wdt:P279 wd:Q215380 }
   }
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 20

dynamic_elements.action_query:
SELECT DISTINCT ?genre ?genreLabel
WHERE { 
  <<item>> wdt:P136 ?genre .

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 10

SELECT DISTINCT ?band ?bandLabel ?category ?categoryLabel ?country ?countryLabel WHERE {
    ?band wdt:P31 ?category ; wdt:P495 wd:Q38 ; wdt:P136 <<item>> .
    ?category wdt:P279 wd:Q2088357 
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 10

dynamic_elements.table_query:
SELECT DISTINCT ?label (COUNT(DISTINCT ?nomination) AS ?count) WHERE {
  ?cremonini ?a wd:Q1056805;
    wdt:P1411 ?nomination.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?nomination rdfs:label ?label.
  }
}
GROUP BY ?label ?count
ORDER BY DESC (?count)
LIMIT 20
