dynamic_elements.chart_query:
SELECT DISTINCT ?label (COUNT(DISTINCT ?occupation) AS ?count) WHERE {
  ?raffaella ?a wd:Q266640;
    wdt:P106 ?occupation.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?occupation rdfs:label ?label.
  }
}
GROUP BY ?label ?count
ORDER BY DESC (?count)
LIMIT 20

dynamic_elements.count_query:
SELECT (COUNT(?singer) AS ?count)
WHERE {
     ?singer ?a wd:Q482994 .
     ?singer wdt:P175 wd:Q266640.
}

dynamic_elements.table_query:
SELECT DISTINCT ?song ?songLabel (YEAR(xsd:dateTime(?publicationDate)) AS ?publication) WHERE {
  ?song wdt:P175 wd:Q266640;
    wdt:P407 wd:Q652;
    wdt:P577 ?publicationDate.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}
ORDER BY (?publicationDate)
LIMIT 10

SELECT DISTINCT ?language ?languageLabel WHERE {
  wd:Q266640 wdt:P1412 ?language.
  ?language rdfs:label ?languageLabel.
  FILTER((LANG(?languageLabel)) = "en")
}

dynamic_elements.chart_query:
SELECT DISTINCT ?label (COUNT(?date) AS ?count) WHERE {
  ?sales ?a wd:Q266640;
    wdt:P577 ?date.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?date rdfs:label ?label.
  }
  FILTER(((YEAR(?date)) >= 1990 ) && ((YEAR(?date)) <= 2020 ))
}
GROUP BY ?label
ORDER BY (?label)

dynamic_elements.table_query:
SELECT DISTINCT ?item ?itemLabel ?sitelinks (GROUP_CONCAT(DISTINCT ?directorLabel; SEPARATOR = ", ") AS ?directors) WHERE {
  ?item wdt:P161 wd:Q266640;
    wikibase:sitelinks ?sitelinks.
  OPTIONAL {
    ?item wdt:P57 ?director.
    ?director rdfs:label ?directorLabel.
    FILTER((LANG(?directorLabel)) = "[AUTO_LANGUAGE]")
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
GROUP BY ?item ?itemLabel ?sitelinks 
ORDER BY DESC (?sitelinks)
LIMIT 30

