SERVICE wikibase:label {  

    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" 

  } 

}

dynamic_elements.table_query:
SELECT DISTINCT (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?DeathPlace ?DeathPlaceLabel  

WHERE {  

wd:Q168485 wdt:P569 ?BirthDate; 

           wdt:P570 ?DeathDate;  

           wdt:P19 ?BirthPlace; 

           wdt:P20 ?DeathPlace .   

SERVICE wikibase:label { bd:serviceParam wikibase:language "en".}   

}

dynamic_elements.chart_query:
SELECT DISTINCT ?label (COUNT(distinct ?composer) as ?count) 

WHERE { 

        	?composer ?a wd:Q36834 ; # composer 

        		      wdt:P19 wd:Q124139 . #Born in Maiolata Spontini 

 

        	SERVICE wikibase:label { bd:serviceParam wikibase:language "en". 

  	  	?composer rdfs:label 	    	?label. 

        	} 

} 

GROUP BY ?label ?count 

ORDER BY DESC(?count)

dynamic_elements.map_points_query:
SELECT DISTINCT ?point ?pointLabel ?lat ?long 

WHERE 

{ 

  wd:Q168485 wdt:P27 ?point . 

  ?point p:P625 ?statement. 

  ?statement psv:P625 ?node. 

  ?node wikibase:geoLatitude ?lat. 

  ?node wikibase:geoLongitude ?long. 

  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". } 

}

dynamic_elements.count_query:
SELECT (COUNT(?composer) AS ?count)  

WHERE {  

?composer wdt:P106 wd:Q36834; #composer 

           wdt:P1066 wd:Q708209 . #studnet of Nicola Sala 

}

