dynamic_elements.table_query:
SELECT DISTINCT ?Member ?MemberLabel (year(xsd:dateTime(?BirthDate)) as ?Birth) (year(xsd:dateTime(?DeathDate)) as ?Death) ?BirthPlace ?BirthPlaceLabel ?VoiceType ?VoiceTypeLabel 
WHERE { wd:Q45188 wdt:P527 ?Member . ?Member wdt:P569 ?BirthDate . 
       OPTIONAL {?Member wdt:P570 ?DeathDate.} ?Member wdt:P19 ?BirthPlace. 
       OPTIONAL {?Member wdt:P412 ?VoiceType.} 
       SERVICE wikibase:label { bd:serviceParam wikibase:language "en".} } 

LIMIT 10

dynamic_elements.textsearch_query:
SELECT DISTINCT ?band ?bandLabel ?category ?categoryLabel ?country ?countryLabel WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search "Coldplay";
                      mwapi:language "en".
      ?band wikibase:apiOutputItem mwapi:item.
  }
  {
   {?band wdt:P31 ?category ; wdt:P495 ?country .
              ?category wdt:P31 wd:Q105756498 . }
    UNION
   {?band wdt:P31 ?category ; wdt:P495 ?country .
    ?category wdt:P279 wd:Q2088357 }
   }
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 1

dynamic_elements.action_query:
SELECT DISTINCT ?genre ?genreLabel
WHERE { 
  <<item>> wdt:P136 ?genre .

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 20

SELECT DISTINCT ?band ?bandLabel ?category ?categoryLabel ?country ?countryLabel WHERE {
    ?band wdt:P31 ?category ; wdt:P495 ?country ; wdt:P136 <<item>> .
    ?category wdt:P279 wd:Q2088357 
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 20

SELECT DISTINCT ?other ?otherLabel ?category ?categoryLabel WHERE { 
  ?other wdt:P495 <<item>> . 
  ?other wdt:P31 ?category .
  ?category wdt:P31 wd:Q105756498 .

SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 20

dynamic_elements.chart_query:
SELECT DISTINCT ?label (COUNT(DISTINCT ?band) AS ?count)
WHERE {
  ?band ?a wd:Q5741069.  # rock band
  ?band wdt:P495 wd:Q145.  # from UK
  ?band wdt:P737 ?influencer.
  
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?influencer rdfs:label ?label.
  }
}
GROUP BY ?label ?count
ORDER BY DESC(?count)
LIMIT 10

dynamic_elements.table_query:
SELECT ?work ?workLabel
WHERE {
  wd:Q45188 wdt:P800 ?work.
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?work rdfs:label ?workLabel.
  }
}

SELECT DISTINCT (?label AS ?album) (YEAR(xsd:dateTime(?date)) AS ?date)
WHERE {
  ?album wdt:P31 wd:Q482994 .
  ?album wdt:P175 wd:Q45188 .
  ?album wdt:P577 ?date .
  
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?album rdfs:label ?label.
  }
}
GROUP BY ?label ?date
ORDER BY ?date
LIMIT 20

dynamic_elements.count_query:
SELECT (COUNT(?band) AS ?count)
WHERE {
     ?band ?a wd:Q484641 . # pop rock band 
     ?band wdt:P495 wd:Q145 . # from uk
}

dynamic_elements.chart_query:
SELECT DISTINCT (YEAR(xsd:dateTime(?date)) AS ?label) (COUNT(?label) AS ?count)
WHERE {
  ?band ?a wd:Q484641. # pop rock band
  ?band wdt:P495 wd:Q145. # from UK
  ?band wdt:P2031 ?date. # formation year

  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?band rdfs:label ?label.
  }
}
GROUP BY ?label ?date
ORDER BY ASC(?label)
LIMIT 20
