dynamic_elements.map_points_query:
SELECT DISTINCT ?point ?artistLabel ?pointLabel ?villeIdLabel ?lat ?long
WHERE
{
{?artist wdt:P31 wd:Q5741069 .
 ?artist wdt:P740 ?point .}
UNION
{?artist wdt:P31 wd:Q5 .
?artist wdt:P136 wd:Q11399 .
?artist wdt:P19 ?point.}
 
  ?point wdt:P706 ?villeId.
  ?point p:P625 ?statement.
  ?statement psv:P625 ?node.
  ?node wikibase:geoLatitude ?lat.
  ?node wikibase:geoLongitude ?long.
 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

dynamic_elements.textsearch_query:
PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX bd: <http://www.bigdata.com/rdf#>
PREFIX wikibase: <http://wikiba.se/ontology#>
PREFIX mwapi: <https://www.mediawiki.org/ontology#API/>

SELECT DISTINCT ?pic ?band ?bandLabel ?activity ?activityLabel ?country ?countryLabel (COUNT(?award) as ?awardCount)
WHERE {
  SERVICE wikibase:mwapi {
    bd:serviceParam wikibase:endpoint "www.wikidata.org";
    wikibase:api "EntitySearch";
    mwapi:search <<searchterm>>;
    mwapi:language "en".
    ?band wikibase:apiOutputItem mwapi:item.
  }
  ?band wdt:P31 wd:Q5741069 . # rock band
  ?band wdt:P495 ?country .
  ?band wdt:P2031 ?year. # start of activity

  OPTIONAL {?band wdt:P18 ?pic; wdt:P166 ?award .}
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
  }
  FILTER regex(str(?pic), "jpg").
  BIND(YEAR(xsd:dateTime(?year)) AS ?activity)
}
GROUP BY ?pic ?band ?bandLabel ?activity ?activityLabel ?country ?countryLabel
LIMIT 10

